/**
 * Color Palette
 * Consistent colors used throughout the app
 */
export const colors = {
  // Primary Colors
  primary: '#3498db',           // Blue - CTAs, links, highlights
  primaryDark: '#2980b9',       // Darker blue - hover states
  
  // Neutral Colors
  darkText: '#2c3e50',          // Dark blue-gray - headings, main text
  bodyText: '#555555',          // Gray - body text
  lightText: '#7f8c8d',         // Light gray - secondary text
  mutedText: '#95a5a6',         // Muted gray - tertiary text
  
  // Background Colors
  background: '#f5f5f5',        // Main background
  cardBackground: '#ffffff',    // Card background
  sectionBg: '#2c3e50',         // Dark section background (hero, footer)
  
  // Accent Colors
  accentLight: '#e8f4f8',       // Light blue - badge background
  border: '#ecf0f1',            // Light gray - borders
  darkBorder: '#34495e',        // Dark border
  
  // Text on Dark Backgrounds
  lightText1: '#ecf0f1',        // Primary light text
  lightText2: '#bdc3c7',        // Secondary light text
};
